import React from 'react'
import { Form, Row, Container,Button } from 'react-bootstrap'
import { makeRequest } from '../../shared/service/currencyConverter.service';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import { Link } from 'react-router-dom'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faExchangeAlt } from '@fortawesome/free-solid-svg-icons'
export default class Converter extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            inputValue: 0,
            inputToCurrency: "",
            inputFromCurrency: "",
            outputValue: ""
        }
    }
    handleValueOnChange = (e) => {
        this.setState({
            inputValue: e.target.value
        })
    }
    handleToInputOnChange = (e) => {
        this.setState({
            inputToCurrency: e.target.value
        })
    }
    handleFromInputOnChange = (e) => {
        this.setState({
            inputFromCurrency: e.target.value
        })
    }
    handleConversion = () => {
        makeRequest(this.state.inputFromCurrency, this.state.inputToCurrency, this.state.inputValue)
            .then(resp => console.log(resp)).catch(error => console.error(error))
    }
    render() {
        return (
            <div className="converter">
                <div className="top-div" style={{ backgroundColor: "#09141c" }}>
                    <h1>pickar</h1>
                    <h2>Convert currencies in real time

                    </h2>
                </div>
                <div className="calculator">
                    <h6>{this.state.outputValue}</h6>
                </div>
                <Row>
                    <Container>
                        <Card className="col-md-9" style={{ borderRadius: "5%" }}>
                            <CardActionArea>
                                <CardContent>
                                    <Typography variant="body2" color="textSecondary">
                                        <Form onSubmit={this.handleConversion}>
                                            <Row>
                                                <Form.Group controlId="inputValue" className="col-md-3 col-sm-12">
                                                    <Form.Label>Amount</Form.Label>
                                                    <Form.Control onChange={this.handleValueOnChange}
                                                        type="number" value={this.state.inputValue} placeholder="1.00" />
                                                </Form.Group>
                                                <Form.Group controlId="inputToCurrency" className="col-md-3 col-sm-12">
                                                    <Form.Label>From</Form.Label>
                                                    <Form.Control as="select">
                                                        <option>EUR</option>
                                                        <option>USD</option>
                                                        <option>CFH</option>
                                                    </Form.Control>
                                                </Form.Group>
                                                <FontAwesomeIcon icon={faExchangeAlt} />
                                                <Form.Group controlId="inputFromCurrency" className="col-md-3 col-sm-12">
                                                    <Form.Label>To</Form.Label>
                                                    <Form.Control as="select">
                                                        <option>EUR</option>
                                                        <option>USD</option>
                                                        <option>CFH</option>
                                                    </Form.Control>
                                                </Form.Group>
                                                <Button type="submit" className="btn btn-warning">Convert</Button>
                                            </Row>
                                        </Form>
                                    </Typography>
                                </CardContent>
                            </CardActionArea>
                        </Card>
                        <Link className="col-md-3" to="/conversion-history">View conversion history</Link>
                    </Container>
                </Row>


            </div>)

    }
}
