import axios from 'axios';
const API_URL = 'http://api.currencylayer.com/';
const access_key="b4a2aa3b60f804e3ab6520add507f0a7";
const endPoint="live"

export function makeRequest(from, to, amount) {
  return (axios.get(`${API_URL}${endPoint}?access_key=${access_key}&from=${from}&to=${to}&amount=${amount}`,
    { headers: { 'Content-Type': 'application/json' } }));
}
