import React from 'react'
import { Form } from 'react-bootstrap'
import makeRequest  from '../../shared/service/currencyConverter.service'
export default class Converter extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            inputValue: 0,
            inputToCurrency: "",
            inputFromCurrency: "",
            outputValue: ""
        }
    }
    handleValueOnChange = (e) => {
        this.setState({
            inputValue: e.target.value
        })
    }
    handleToInputOnChange = (e) => {
        this.setState({
            inputToCurrency: e.target.value
        })
    }
    handleFromInputOnChange = (e) => {
        this.setState({
            inputFromCurrency: e.target.value
        })
    }
    handleConversion = () => {
      makeRequest(this.state.inputFromCurrency, this.state.inputToCurrency, this.state.inputValue)
      .then(resp=>console.log(resp)).catch(error=>console.error(error))
    }
    render() {
        return (
            <div className="converter">
                <div className="top-div" style={{ backgroundColor: "#09141c" }}>
                    <h1>pickar</h1>
                    <h2>Convert currencies in real time

                    </h2>
                </div>
                <div className="calculator">
                    <Form onSubmit={this.handleConversion}>
                        <input type="text" name="inputValue" onChange={this.handleValueOnChange} value={this.state.inputValue} />
                    </Form>
                </div>
            </div>)

    }
}
